import logo from './logo.svg';
import './App.css';
import react from 'react';
import Header from './MyComponent/Header';
import Footer from './MyComponent/Footer';
import Customertext from './MyComponent/Customertext';
import React, { useState,useEffect } from 'react';


function App() {

  const [orders, setOrders] = useState([]);

   useEffect(() => {
    localStorage.setItem('orders',orders);
  }, []);


  // Customer order
  const addOrder = (name,order) => {
    setOrders([...orders, { name, order }]);
  }

  console.log("I am adding a new  Oredr",orders);
  return (
    <>
    <Header title="Online Food"/>
    <Customertext onOrderSubmit={addOrder}/>
  {
    orders.map((orders)=><p className='text-center'>{orders.name}={orders.order}</p>)
  }  
    <Footer/>
    </>
  );
}

export default App;
