import logo from './logo.svg';
import './App.css';
import react from 'react';
import Header from './MyComponent/Header';
import Footer from './MyComponent/Footer';
import Customertext from './MyComponent/Customertext';
import React, { useState,useEffect } from 'react';


function App() {

  const [orders, setOrders] = useState([]);

   useEffect(() => {
    localStorage.setItem('orders',orders);
  }, []);


  // Customer order
  const addOrder = (name,order) => {
    setOrders([...orders, { name, order }]);
  }

  console.log("I am adding a new  Oredr",orders);

   let myStyle={
    minHeight:"45vh"
  }
  return (

    <>
    <div className='container' style={myStyle}>
    <Header title="Online Food"/>
    <Customertext onOrderSubmit={addOrder}/>
  {
  orders.map((orders) => (
    <div className='container' >
      <h1 className='text-center'>{orders.name} </h1>
      <p className='text-center'> {orders.order}</p>
      <hr />
    </div>
  ))
}

    <Footer/>
    </div>
    </>
  );
}

export default App;
