import logo from './logo.svg';
import './App.css';
import react from 'react';
import Header from './MyComponent/Header';
import Footer from './MyComponent/Footer';
import Customertext from './MyComponent/Customertext';
import React, { useState,useEffect } from 'react';


function App() {

  const [orders, setOrders] = useState(() => {
    const saved = localStorage.getItem('orders');
    return saved ? JSON.parse(saved) : [];
  });

  useEffect(() => {
    localStorage.setItem('orders', JSON.stringify(orders));
  }, [orders]);


  // Customer order
  const addOrder = (name,order) => {
    console.log("I am adding a new  Oredr",name,order);
    setOrders([...orders, { name, order }]);
  }
  return (
    <>
    <Header title="Online Food"/>
    <Customertext onOrderSubmit={addOrder}/>
    <Footer/>
    </>
  );
}

export default App;
